<form id="loginform" class="form-vertical" action="/" method="post">
    <div class="control-group normal_text"> <h3><img src="img/logo.png" alt="Logo" />某管理系统</h3></div>
    <div class="control-group">
        <div class="controls">
            <div class="main_input_box">
                <span class="add-on bg_lg"><i class="icon-user"></i></span><input type="text" placeholder="Username" />
            </div>
        </div>
    </div>
    <div class="control-group">
        <div class="controls">
            <div class="main_input_box">
                <span class="add-on bg_ly"><i class="icon-lock"></i></span><input type="password" placeholder="Password" />
            </div>
        </div>
    </div>
    <div class="form-actions">
        <span class="pull-left"><a href="#" class="flip-link btn btn-info" id="to-recover">Lost password?</a></span>
        <span class="pull-right"><button type="submit" href="index.html" class="btn btn-success" /> Login</a></span>
    </div>
</form>